package com.example.recyclerview.view

import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.navigation.fragment.findNavController
import androidx.recyclerview.widget.LinearLayoutManager
import com.example.recyclerview.adapter.CarAdapter
import com.example.recyclerview.adapter.CarModel
import com.example.recyclerview.databinding.FragmentListBinding

class ListFragment : Fragment() {

    private val binding: FragmentListBinding by lazy{
        FragmentListBinding.inflate(layoutInflater)
    }

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View {

        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)

        findNavController()
        val url = "data:image/jpeg;base64,"
        val list = listOf(
            CarModel(url, "crv", "2007"),
            CarModel(url, "camry", "2004"),
            CarModel(url, "hunday", "2005"),
            CarModel(url, "sola", "2004"),
            CarModel(url, "fdbf", "1999"),
        )
        val adapter = CarAdapter(list)
        binding.rvCars.layoutManager = LinearLayoutManager(requireContext(), LinearLayoutManager.VERTICAL, false)
        binding.rvCars.adapter = adapter
    }
}